import { useHistory } from 'react-router';
import { gql, useQuery } from '@apollo/client';

const LINKS_PER_PAGE = 2;
const ARTICLES_QUERY = gql`
  query Articles{
    articles{
      slug
      title
      content
      description
      author{
        name
      }
      category {
        slug
        name
      }
      image {
        url
      }
    }
  }
`;

export default ARTICLES_QUERY;
