import React, { useState } from 'react';
import axios from 'axios';
import { setUserSession } from './Utils/Common';

function Login(props) {
  const [loading, setLoading] = useState(false);
  const username = useFormInput('');
  const password = useFormInput('');
  const [error, setError] = useState(null);

  const handleLogin = () => {
    setError(null);
    setLoading(true);
    axios.post('https://reqres.in/api/login', { username: username.value, password: password.value }).then(response => {
      setLoading(false);
      setUserSession(response.data.token, response.data.user);
      props.history.push('/user');
    }).catch(error => {
      setLoading(false);
      if (error.response.status === 401) setError(error.response.data.message);
      else setError("Something went wrong. Please try again later.");
    });
  }

  return (
            <section class="py-5">
              <div class="container px-5">
                <div class="row justify-content-center">
                  <div class="col-lg-6">
                    <div class="card">
                      <form class="py-5 px-5 biru text-white">
                        <h2 class="fw-bolder text-center mb-3">Login</h2>
                        <div class="form-group mb-3">
                          <label class="mb-1">Email</label>
                          <input type="email" {...username} className="form-control" placeholder="Enter email" />
                        </div>
                        <div className="form-group mb-3">
                          <label class="mb-1">Password</label>
                          <input type="password" {...password} className="form-control" placeholder="Enter password" />
                        </div>
                        {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}
                        <button type="submit" value="Kirim" class="btn btn-light"
                        value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} >Kirim</button>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </section>
  );
}

const useFormInput = initialValue => {
  const [value, setValue] = useState(initialValue);

  const handleChange = e => {
    setValue(e.target.value);
  }
  return {
    value,
    onChange: handleChange
  }
}

export default Login;
