import React, { useState, useEffect } from "react";
import "../../index.css";
import { Link } from "react-router-dom";



export default class UserList extends React.Component{
  constructor(props){
    super(props);
    this.state={
      users:[],
    }
    this.logout = this.logout.bind(this);
  }

    async componentDidMount() {
        try { 
          const res = await fetch("https://reqres.in/api/users/");
          const users = await res.json();
          this.setState({
            user
          });
        } catch (e) {
          console.log(e);
          this.setState({ errorMessage: "Course Tidak Tersedia" });
        }
    }

  logout(){
    sessionStorage.setItem("data",'');
    sessionStorage.clear();
    this.setState({redirectToReferrer: true});
  }
  render(){
  return (
        <section class="py-5 bg-light">
          <div class="container px-5 my-5">
            <div class="text-center mb-5">
              <h1 class="fw-bolder">List User</h1>
            </div>

            <div class="row gx-5 content-center">
              {this.state.users.map(user => {
                  return (
                    <div key={user.id} class="col-xl-4 mb-5">
                      <Link to={`/user/${user.id}`} className="text-reset text-decoration-none">
                        <div class="card mb-5 mb-xl-0">
                          <div class="card-body p-5">
                            <div class="mb-3 text-center">
                              <span class="fw-bolder"><img src={user.avatar} /></span>
                            </div>
                            <div class="text-center">
                              <span>{user.first_name}</span>
                              <p>{user.email}</p>
                            </div>
                          </div>
                        </div>
                      </Link>
                    </div>
                  );
                })}
            </div>

          </div>
        </section>
  );
}
}
