import React from "react";
import { useParams } from "react-router";
import Query from "../../components/Query";
import ReactMarkdown from "react-markdown";
import Moment from "react-moment";
import ARTICLE_QUERY from "../../queries/article/article";
import {StickyShareButtons} from 'sharethis-reactjs';
import { useLocation } from 'react-router-dom';

const Article = () => {
  let { id } = useParams();

  return (
    <Query query={ARTICLE_QUERY} slug={id}>
      {({ data: { articles } }) => {

        if (articles.length) {
          const imageUrl =
            process.env.NODE_ENV !== "development"
              ? articles[0].image.url
              : process.env.REACT_APP_BACKEND_URL + articles[0].image.url;

          return (
          <header className="py-5 px-3">
            <div className="container px-5 py-5">
              <div class="jumbotron"> 
                <h1 class="text-center mb-5">{articles[0].title}</h1>   
                <p>
                  Posted by {articles[0].author.name} at <Moment format="MMM Do YYYY">
                  {articles[0].published_at}</Moment>
                </p>
                <div class="mb-5">
                  <img
                    src={imageUrl}
                    alt={articles[0].image.url}
                  />
                </div>
                <ReactMarkdown children={articles[0].content} />
              </div>
              <StickyShareButtons
                config={{
                  alignment: 'left',
                  color: 'social',
                  enabled: true,
                  font_size: 16,
                  hide_desktop: false,
                  labels: 'counts',    
                  language: 'en',
                  min_count: 0,
                  networks: [
                    'whatsapp',
                    'telegram',
                    'linkedin',
                    'instagram',
                    'messenger',
                    'facebook',
                    'twitter',
                  ],
                  padding: 12,
                  radius: 4,
                  show_total: false, 
                  show_mobile: false,
                  show_toggle: true,
                  size: 48,
                  top: 140,
                  url: window.location.href,
                  image: imageUrl,
                  description: articles[0].description,
                  title: articles[0].title
                }}
              />
            </div>
          </header>
          );
        }
      }}
    </Query>
  );
};

export default Article;
