import gql from "graphql-tag";

const ARTICLES_QUERY = gql`
  query Articles {
    $take: 3
    $orderBy: title
    articles(take: $take, skip: $skip, orderBy: $orderBy){
      slug
      title
      content
      description
      author{
        name
      }
      category {
        slug
        name
      }
      image {
        url
      }
    }
  }
`;

export default ARTICLES_QUERY;
