import gql from "graphql-tag";

function query(limit, offset) {

  var dataset; // gotten from database

  var start = false;

  var result = [];

  for (let i = 0; i <= dataset.length; i++) {

    if (i >= offset) {

      result.push(dataset[i]);

    }

    if (limit && result.length === limit) {

      break;

    }

  }

}

const ARTICLES_QUERY = gql`
  query Articles {
    articles(limit: 2, cursor: 1){
      slug
      title
      content
      description
      author{
        name
      }
      category {
        slug
        name
      }
      image {
        url
      }
    }
  }
`;

export default ARTICLES_QUERY;
