import React from 'react';
import axios from 'axios';
import {Redirect} from 'react-router-dom';

export default class Login extends React.Component{
    constructor(props){
        super(props);
        this.state = {
           email : '' ,
           password : '',
           redirectToReferrer : false
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }



    handleChange(event){
            this.setState({
                [event.target.name] : event.target.value

            });
    }
    handleSubmit(event){
            event.preventDefault();

            const user = {
            email : this.state.email,
            password : this.state.password
            };

      if(this.state.email && this.state.password)      
        {
     axios.post(`https://reqres.in/api/login`,user)
     .then((response) =>
        { 
            let userresponse = response;
            console.log(userresponse.data);
            if(userresponse){
            sessionStorage.setItem('data',JSON.stringify(userresponse));
            this.setState({redirectToReferrer: true});
            }

        },this)
        .catch((error) => alert(error))

    }
}

render(){
    if (this.state.redirectToReferrer){

        return (<Redirect to={'/user'}/>)
        }
        if (sessionStorage.getItem('data')){

            return (<Redirect to={'/user'}/>)
            }
    return(
                  <section class="py-5">
              <div class="container px-5">
                <div class="row justify-content-center">
                  <div class="col-lg-6">
                    <div class="card">
                      <form class="py-5 px-5 biru text-white" onSubmit={this.handleSubmit}>
                        <h2 class="fw-bolder text-center mb-3">Login</h2>
                        <div class="form-group mb-3">
                          <label class="mb-1">Email</label>
                          <input type="email" className="form-control" placeholder="Enter email" onChange={this.handleChange} required/>
                        </div>
                        <div className="form-group mb-3">
                          <label class="mb-1">Password</label>
                          <input type="password" className="form-control" placeholder="Enter password" onChange={this.handleChange} required/>
                        </div>
                        <button type="submit" value="Kirim" class="btn btn-light">Kirim</button>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </section>
    )
}
}
