import gql from "graphql-tag";

const ARTICLE_QUERY = gql`
  query Article($slug: String!){
    $take: 3
    $orderBy: title
    articles(where: {slug: $slug} take: $take, orderBy: $orderBy) {
      slug
      title
      content
      description
      author{
        name
      }
      category {
        slug
        name
      }
      image {
        url
      }
    }
  }
`;

export default ARTICLE_QUERY;
