import React from "react";
import Articles from "../../components/Articles";
import Query from "../../components/Query";
import ARTICLES_QUERY from "../../queries/article/articles";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div>
      <div className="container py-3">
        <h1 class="my-4 px-3">
          <nav aria-label="breadcrumb">
            <ol class="breadcrumb">
              <li class="breadcrumb-item">
                <Link className="text-decoration-none" to="/">Blog</Link>
              </li>  
            </ol>
          </nav>
        </h1>
      </div>
      <Query query={ARTICLES_QUERY}>
        {({ data: { articles } }) => {
          return (
            <div className="newslist">
              <div className="newslistbreadcrumb">
                <div className="newslisttitle">
                  <h3>World News</h3>
                </div>
                <div style={{ display: "flex", alignItems: "center" }}>
                  <div style={{ marginRight: "4px" }}>
                    <button onClick={showAddNewsDialog}>Add News</button>
                  </div>
                </div>
              </div>
              <div>
                {newsList
                  ?.sort((a, b) => b.created_at.localeCompare(a.created_at))
                  ?.map((articles, i) => (
                    <NewsCard Articles articles={articles} key={i} />
                  ))}
              </div>
              {showModal ? <AddNewsDialog closeModal={showAddNewsDialog} /> : null}
              <div>
                <span>
                  <button disabled={limit > start} onClick={prevPage}>
                    Prev
                  </button>
                </span>
                <span>
                  <button
                    disabled={pageDetails && start + limit >= pageDetails?.totalCount}
                    onClick={nextPage}
                  >
                    Next
                  </button>
                </span>
              </div>
            </div>
          );
        }}
      </Query>
    </div>
  );
};

export default Home;
