import React from "react";
import Trial from './'
import { Link } from 'react-router-dom';

class Brewery extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      breweries: []
    };
  }

  componentDidMount() {   
    fetch("https://api.openbrewerydb.org/breweries")
      .then(response => response.json())
      .then((data) => {
        this.setState({
          breweries: data,
        })
      })
  }

  render() {
    const { breweries } = this.state;
    return(
          <div class="bg-light">
            <div class="container">
              <div class="text-center py-5">
                <h1 class="fw-bolder">List Breweries</h1>
              </div>

              <table>
                <tr>
                  <th>Name</th>
                  <th>City</th>
                  <th>State</th>
                  <th>Postal Code</th>
                  <th>Country</th>
                </tr>
                {breweries.map((brewery) => {
                  return (
                    <tr key={brewery.id}>
                      <td>{brewery.name}</td>
                      <td>{brewery.city}</td>
                      <td>{brewery.state}</td>
                      <td>{brewery.postal_code}</td>
                      <td>{brewery.country}</td>
                    </tr>
                  );
                })}
              </table>

              <div class="row gx-5 content-center">
                {breweries.map((brewery) => {
                    return (
                      <div key={brewery.id} class="col-xl-4 mb-5">
                        <div class="card border-dark mb-3">
                          <div class="card-header text-center">{brewery.name}</div>
                          <div class="card-body text-dark">
                            <ul class="list-unstyled">
                               <li>Name : {brewery.name}</li>
                               <li>City : {brewery.city}</li>
                               <li>State : {brewery.state}</li>
                               <li>Postal Code : {brewery.postal_code}</li>
                               <li>Country : {brewery.country}</li>
                            </ul>
                          </div>
                        </div>
                      </div>
                    );
                  })}
              </div>

            </div>
          </div>
    )}}

export default Brewery;
